package PracticeTest2;

import java.util.Scanner;

public class Q2 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
//2.	Write a program to find the factorial of a number using a for loop.
		
		
		
		//find: 
		//Scanner: numb
		Scanner kb = new Scanner(System.in);
		
		System.out.println("give me a positive integer ");
		int numb = kb.nextInt();
		kb.close();
		
		//factorial number: 4! = 4*3*2*1
		// Factorial = numb * (numb
		// for (i=1; i<=numb; i++)
		//for (loop)
		int fac = 1;
		int i;
		for (i = 1; i<=numb ; i++) {
			fac = fac*i;
		}
		System.out.println(fac);
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
	}

}
